// Dashboard.js (main page for logged-in users)

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import NewInCanada from './NewInCanada';

export default function Dashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState(null);

  useEffect(() => {
    const stored = localStorage.getItem('user') || localStorage.getItem('registeredUser');
    if (stored) {
      setUser(JSON.parse(stored));
    } else {
      navigate('/login-ui');
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('registeredUser');
    navigate('/login-ui');
  };

  const services = [
    { icon: '‚úàÔ∏è', title: 'Airport Pickup & Settlement Help', desc: 'Smooth landing and support from day 1' },
    { icon: 'üè†', title: 'Housing Assistance', desc: 'Shared rooms, rental tips, and landlord advice' },
    { icon: 'üìÑ', title: 'SIN, Bank, ID Setup', desc: 'Documents and accounts made easy' },
    { icon: 'üéì', title: 'Student Support', desc: 'Colleges, PGWP, study visa help' },
    { icon: 'üíº', title: 'Job Connection', desc: 'Resumes, job boards, and referrals' },
    { icon: 'ü§ù', title: 'Community Support', desc: 'Meetups, culture and connection' },
    { icon: 'üìö', title: 'Language Support', desc: 'IELTS prep, French and translation' },
    { icon: 'üõçÔ∏è', title: 'Nepali Directory', desc: 'Shops, food, services near you' },
    { icon: 'üßæ', title: 'Legal/Immigration Help', desc: 'PR pathway, lawyers and resources' },
  ];

  const checklist = [
    'Apply for SIN (Social Insurance Number)',
    'Open a Canadian bank account',
    'Get a local SIM card',
    'Apply for a health card (MSP/OHIP)',
    'Learn about public transit (Compass/Presto)',
    'Build your credit history',
    'Start looking for housing',
    'Register for newcomer orientation programs',
  ];

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-bold text-gray-800">Welcome, {user?.name || 'User'}</h1>
          <p className="text-sm text-gray-600">Your Referral Code: <strong>{user?.referralCode}</strong></p>
          <p className="text-sm text-gray-600">
            Invite friends: 
            <span className="text-blue-700 font-medium break-all">
              https://connectingnepali.com/register?ref={user?.referralCode}
            </span>
          </p>
        </div>
        <button onClick={handleLogout} className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700">
          Logout
        </button>
      </div>

      {/* Services Grid */}
      <h2 className="text-xl font-semibold mb-4">Explore Services</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-10">
        {services.map((service, idx) => (
          <div key={idx} className="bg-white shadow-md rounded p-5 hover:shadow-lg">
            <div className="text-3xl mb-3">{service.icon}</div>
            <h3 className="text-lg font-bold mb-1">{service.title}</h3>
            <p className="text-sm text-gray-600 mb-3">{service.desc}</p>
            <button className="text-blue-600 hover:underline">Learn More</button>
          </div>
        ))}
      </div>

      {/* To-do Checklist */}
      <div className="bg-white shadow-md rounded p-6 mb-6">
        <h2 className="text-xl font-semibold mb-4">Things to Do When You Arrive in Canada</h2>
        <ul className="list-disc pl-6 space-y-2 text-gray-700">
          {checklist.map((item, index) => (
            <li key={index}>{item}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}
