// src/pages/LoginUI.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function LoginUI() {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('https://connectingnepali.onrender.com/api/auth/login', form);
      localStorage.setItem('token', res.data.token);
      // Optional: Save user info too
      localStorage.setItem('user', JSON.stringify(res.data.user));
      // Use full reload to guarantee token access
      window.location.href = '/dashboard';
    } catch (err) {
      setMessage(err.response?.data?.error || 'Login failed');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 relative">
      {/* Header */}
      <div className="absolute top-4 right-4 flex gap-4">
        <button onClick={() => navigate('/login-ui')} className="bg-blue-600 text-white px-4 py-2 rounded">Login</button>
        <button onClick={() => navigate('/register')} className="bg-gray-700 text-white px-4 py-2 rounded">Register</button>
      </div>

      {/* Login Form */}
      <div className="flex items-center justify-center h-screen">
        <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md w-96">
          <h2 className="text-2xl font-bold mb-4 text-center">Login</h2>
          {message && <p className="mb-3 text-red-500 text-center">{message}</p>}
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={form.email}
            onChange={handleChange}
            className="mb-3 w-full px-3 py-2 border rounded"
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={form.password}
            onChange={handleChange}
            className="mb-3 w-full px-3 py-2 border rounded"
            required
          />
          <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700">
            Login
          </button>
        </form>
      </div>
    </div>
  );
}
