import React, { useEffect, useState } from 'react';
import { Card, CardContent } from '../components/Card';
import { Button } from '../components/Button';
import { Link } from 'react-router-dom';

export default function DashboardServices() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      setUser(JSON.parse(userData));
    }
  }, []);

  const services = [
    {
      title: 'Airport Pickup & Settlement Help',
      description: 'Smooth landing and support from day 1',
      icon: '‚úàÔ∏è',
      link: '#'
    },
    {
      title: 'Housing Assistance',
      description: 'Shared rooms, rental tips, and landlord advice',
      icon: 'üè†',
      link: '#'
    },
    {
      title: 'SIN, Bank, ID Setup',
      description: 'Documents and accounts made easy',
      icon: 'üìÑ',
      link: '#'
    },
    {
      title: 'Student Support',
      description: 'Colleges, PGWP, study visa help',
      icon: 'üéì',
      link: '#'
    },
    {
      title: 'Job Connection',
      description: 'Resumes, job boards, and referrals',
      icon: 'üíº',
      link: '#'
    },
    {
      title: 'Community Support',
      description: 'Meetups, culture and connection',
      icon: 'ü§ù',
      link: '#'
    },
    {
      title: 'Language Support',
      description: 'IELTS prep, French and translation',
      icon: 'üìö',
      link: '#'
    },
    {
      title: 'Nepali Directory',
      description: 'Shops, food, services near you',
      icon: 'üõçÔ∏è',
      link: '#'
    },
    {
      title: 'Legal/Immigration Help',
      description: 'PR pathway, lawyers and resources',
      icon: 'üßæ',
      link: '#'
    },
    {
      title: 'New in Canada',
      description: 'Step-by-step help for newcomers',
      icon: 'üÜï',
      link: '/new-in-canada'
    }
  ];

  return (
    <div className="p-6">
      {user && (
        <div className="mb-6 p-4 rounded-xl shadow bg-white max-w-xl mx-auto text-center">
          <h2 className="text-2xl font-bold mb-1">Welcome, {user.name} üëã</h2>
          <p className="text-gray-600">{user.email}</p>
        </div>
      )}

      <h1 className="text-3xl font-bold mb-6 text-center">Explore Our Services</h1>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {services.map((service, index) => (
          <Card key={index} className="rounded-lg shadow">
            <CardContent className="p-5">
              <div className="text-3xl mb-2">{service.icon}</div>
              <h3 className="text-xl font-semibold mb-1">{service.title}</h3>
              <p className="text-gray-600 mb-3">{service.description}</p>
              <Link to={service.link} className="text-blue-600 hover:underline">
                Learn More
              </Link>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );
}
