import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Dashboard() {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('jobs');

  // Load user
  const user = (() => {
    try {
      const stored = localStorage.getItem('user');
      return stored ? JSON.parse(stored) : { name: 'Guest' };
    } catch {
      return { name: 'Guest' };
    }
  })();

  const [currentTime, setCurrentTime] = useState(new Date());
  useEffect(() => {
    const interval = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);
  const formattedTime = currentTime.toLocaleTimeString();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/');
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-4xl mx-auto bg-white shadow rounded p-6">
        <div className="flex justify-between items-center mb-6">
          <div>
            <h1 className="text-2xl font-bold">Hello, {user.name}</h1>
            <p className="text-sm text-gray-500">Current Time: {formattedTime}</p>
          </div>
          <button
            onClick={handleLogout}
            className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded"
          >
            Logout
          </button>
        </div>

        {/* Tabs */}
        <div className="flex border-b mb-4">
          <button
            onClick={() => setActiveTab('jobs')}
            className={`px-4 py-2 font-medium ${activeTab === 'jobs' ? 'border-b-2 border-blue-600 text-blue-600' : 'text-gray-500'}`}
          >
            Job Postings
          </button>
          <button
            onClick={() => setActiveTab('comments')}
            className={`ml-4 px-4 py-2 font-medium ${activeTab === 'comments' ? 'border-b-2 border-green-600 text-green-600' : 'text-gray-500'}`}
          >
            Comments
          </button>
        </div>

        {/* Tab Content */}
        {activeTab === 'jobs' && (
          <div>
            <h2 className="text-xl font-semibold mb-2">Post a Job</h2>
            {/* Add form to post jobs */}
            <form className="space-y-4">
              <input
                type="text"
                placeholder="Job Title"
                className="w-full border px-3 py-2 rounded"
              />
              <textarea
                placeholder="Job Description"
                className="w-full border px-3 py-2 rounded"
              />
              <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
                Post Job
              </button>
            </form>
          </div>
        )}

        {activeTab === 'comments' && (
          <div>
            <h2 className="text-xl font-semibold mb-2">Comments on Job Postings</h2>
            {/* Comments UI */}
            <textarea
              placeholder="Write a comment..."
              className="w-full border px-3 py-2 rounded mb-4"
            />
            <button className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700">
              Submit Comment
            </button>
            <div className="mt-6">
              <p className="text-gray-700">No comments yet.</p>
              {/* Later populate with comment list */}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
