import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Dashboard() {
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) navigate('/');
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  const services = [
    { name: 'Jobs', description: 'Find and post jobs globally', icon: 'üíº' },
    { name: 'Blogs', description: 'Read and share Nepali stories', icon: 'üìù' },
    { name: 'Events', description: 'Join and create cultural events', icon: 'üìÖ' },
    { name: 'Chat', description: 'Connect with Nepali worldwide', icon: 'üí¨' },
    { name: 'Business Directory', description: 'Support Nepali businesses', icon: 'üè™' },
    { name: 'Help Desk', description: 'Ask questions & get support', icon: 'üõ†Ô∏è' },
  ];

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="flex justify-between items-center mb-6">
        <div className="flex items-center space-x-3">
          <img src="/logo.png" alt="Logo" className="w-12" />
          <h1 className="text-2xl font-bold">ConnectingNepali Dashboard</h1>
        </div>
        <button
          onClick={handleLogout}
          className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        >
          Logout
        </button>
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {services.map((service, index) => (
          <div key={index} className="bg-white p-6 rounded shadow hover:shadow-lg transition">
            <div className="text-4xl mb-2">{service.icon}</div>
            <h2 className="text-xl font-semibold">{service.name}</h2>
            <p className="text-gray-600">{service.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
