import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function LoginPage() {
  const [form, setForm] = useState({ email: '', password: '' });
  const [registerForm, setRegisterForm] = useState({ name: '', email: '', password: '' });
  const [forgotEmail, setForgotEmail] = useState('');
  const [message, setMessage] = useState('');
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleRegisterChange = (e) => {
    setRegisterForm({ ...registerForm, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('https://connectingnepali.onrender.com/api/auth/login', form);
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      window.location.href = '/dashboard';
    } catch (err) {
      setMessage(err.response?.data?.error || 'Login failed');
    }
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('https://connectingnepali.onrender.com/api/auth/register', registerForm);
      setMessage('Registration successful! Redirecting to login...');
      setShowRegister(false);
      setShowLogin(true);
    } catch (err) {
      setMessage(err.response?.data?.error || 'Registration failed');
    }
  };

  const handleForgotPasswordSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/auth/forgot-password', { email: forgotEmail });
      setMessage('Password reset link sent to your email');
      setShowForgotPassword(false);
    } catch (err) {
      setMessage(err.response?.data?.error || 'Failed to send reset link');
    }
  };

  const serviceBlocks = [
    { icon: 'üó∫Ô∏è', title: 'New in Canada', desc: 'Arrival guide and newcomer essentials', path: '/services/new-to-canada' },
    { icon: 'üõ¨', title: 'Airport Pickup & Settlement Help', desc: 'Smooth landing and support from day 1', path: '/services/airport-pickup-settlement-help' },
    { icon: 'üè†', title: 'Housing Assistance', desc: 'Shared rooms, rental tips, and landlord advice', path: '/services/housing-assistance' },
    { icon: 'üìÑ', title: 'SIN, Bank, ID Setup', desc: 'Documents and accounts made easy', path: '/services/sin-bank-id-setup' },
    { icon: 'üéì', title: 'Student Support', desc: 'Colleges, PGWP, study visa help', path: '/services/student-support' },
    { icon: 'üíº', title: 'Job Connection', desc: 'Resumes, job boards, and referrals', path: '/services/job-connection' },
    { icon: 'ü§ù', title: 'Community Support', desc: 'Meetups, culture and connection', path: '/services/community-support' },
    { icon: 'üìö', title: 'Language Support', desc: 'IELTS prep, French and translation', path: '/services/language-support' },
    { icon: 'üõçÔ∏è', title: 'Nepali Directory', desc: 'Shops, food, services near you', path: '/services/nepali-directory' },
    { icon: '‚öñÔ∏è', title: 'Legal/Immigration Help', desc: 'PR pathway, lawyers and resources', path: '/services/legal-immigration-help' }
  ];

  return (
    <div className="min-h-screen bg-white">
      {/* Top Header */}
      <div className="flex justify-between items-center p-4 shadow-md">
        <div className="text-lg font-bold">NepaliCircle</div>
        <div className="space-x-4">
          <button onClick={() => setShowLogin(true)} className="px-4 py-2 border rounded">Login</button>
          <button onClick={() => setShowRegister(true)} className="px-4 py-2 border rounded">Register</button>
        </div>
      </div>

      {/* Home Banner Section */}
      <div className="bg-gray-100 px-6 py-8 text-center">
        <h1 className="text-4xl font-bold mb-2">Welcome to NepaliCircle</h1>
        <p className="text-lg mb-6">‡§ú‡§π‡§æ‡§Å ‡§®‡•á‡§™‡§æ‡§≤‡•Ä ‡§Æ‡§®‡§π‡§∞‡•Ç ‡§ú‡•ã‡§°‡§ø‡§®‡•ç‡§õ‡§®‡•ç</p>
        <button onClick={() => setShowRegister(true)} className="bg-blue-600 text-white px-6 py-3 rounded hover:bg-blue-700">Get Started</button>
      </div>

      {/* Service Grid */}
      <div className="p-8">
        <h2 className="text-2xl font-semibold mb-4">Explore Services</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {serviceBlocks.map((service, idx) => (
            <div key={idx} className="border p-4 rounded shadow hover:shadow-md transition">
              <h3 className="font-semibold text-lg mb-1 flex items-center gap-2">
                <span>{service.icon}</span> {service.title}
              </h3>
              <p className="text-sm text-gray-600 mb-2">{service.desc}</p>
              <button
                onClick={() => navigate(service.path)}
                className="text-blue-600 hover:underline text-sm"
              >
                Learn More
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* The modals remain unchanged */}
      {/* Login Modal */}
      {showLogin && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">...</div>
      )}

      {/* Register Modal */}
      {showRegister && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">...</div>
      )}

      {/* Forgot Password Modal */}
      {showForgotPassword && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">...</div>
      )}
    </div>
  );
}
