import React, { useState } from 'react';
import API from '../api';
import { useNavigate } from 'react-router-dom';


export default function AuthPage() {
  const [activeTab, setActiveTab] = useState('login');
  const [loginForm, setLoginForm] = useState({ email: '', password: '' });
  const [registerForm, setRegisterForm] = useState({ name: '', email: '', password: '' });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleLoginChange = (e) =>
    setLoginForm({ ...loginForm, [e.target.name]: e.target.value });

  const handleRegisterChange = (e) =>
    setRegisterForm({ ...registerForm, [e.target.name]: e.target.value });

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await API.post('/auth/login', loginForm);
      localStorage.setItem('token', res.data.token);
      navigate('/dashboard');
    } catch (err) {
      setMessage(err.response?.data?.error || 'Login failed');
    }
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    try {
      await API.post('/auth/register', registerForm);
      setMessage('Registered successfully!');
      setActiveTab('login');
    } catch (err) {
      setMessage(err.response?.data?.error || 'Registration failed');
    }
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-100">
      <div className="bg-white p-6 rounded shadow-md w-full max-w-md">
        <div className="flex justify-around mb-4 border-b">
          <button
            onClick={() => { setActiveTab('login'); setMessage(''); }}
            className={`px-4 py-2 font-medium ${activeTab === 'login' ? 'border-b-2 border-blue-500' : 'text-gray-500'}`}
          >
            Login
          </button>
          <button
            onClick={() => { setActiveTab('register'); setMessage(''); }}
            className={`px-4 py-2 font-medium ${activeTab === 'register' ? 'border-b-2 border-green-500' : 'text-gray-500'}`}
          >
            Register
          </button>
        </div>

        {activeTab === 'login' ? (
          <form onSubmit={handleLoginSubmit} className="space-y-4">
            <input
              type="email"
              name="email"
              placeholder="Email"
              onChange={handleLoginChange}
              required
              className="w-full border px-3 py-2"
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              onChange={handleLoginChange}
              required
              className="w-full border px-3 py-2"
            />
            <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700">
              Login
            </button>
          </form>
        ) : (
          <form onSubmit={handleRegisterSubmit} className="space-y-4">
            <input
              type="text"
              name="name"
              placeholder="Full Name"
              onChange={handleRegisterChange}
              required
              className="w-full border px-3 py-2"
            />
            <input
              type="email"
              name="email"
              placeholder="Email"
              onChange={handleRegisterChange}
              required
              className="w-full border px-3 py-2"
            />
            <input
              type="password"
              name="password"
              placeholder="Password"
              onChange={handleRegisterChange}
              required
              className="w-full border px-3 py-2"
            />
            <button type="submit" className="w-full bg-green-600 text-white py-2 rounded hover:bg-green-700">
              Register
            </button>
          </form>
        )}
        {message && <p className="mt-4 text-center text-red-500">{message}</p>}
      </div>
    </div>
  );
}
