import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function Dashboard() {
  const navigate = useNavigate();
  const [news, setNews] = useState([]);

  const services = [
    { name: 'Jobs', description: 'Find and post jobs globally', icon: '💼' },
    { name: 'Blogs', description: 'Read and share Nepali stories', icon: '📝' },
    { name: 'Events', description: 'Join and create cultural events', icon: '📅' },
    { name: 'Chat', description: 'Connect with Nepali worldwide', icon: '💬' },
    { name: 'Business Directory', description: 'Support Nepali businesses', icon: '🏪' },
    { name: 'Help Desk', description: 'Ask questions & get support', icon: '🛠️' },
  ];

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) navigate('/');
    fetchNepaliNews();
  }, []);

  const fetchNepaliNews = async () => {
    try {
      const response = await axios.get('https://gnews.io/api/v4/search', {
        params: {
          q: 'Nepal',
          lang: 'en',
          country: 'np',
          max: 10,
          token: '8146efcc1bf5099da3081ea9d8b17bfd' // 🔁 Replace with your real API key
        }
      });
      setNews(response.data.articles.map(article => article.title));
    } catch (error) {
      console.error('Failed to fetch news:', error);
      setNews(["Unable to load live news. Please try again later."]);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/');
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="flex justify-between items-center mb-6">
        <div className="flex items-center space-x-3">
          <img src="/logo.png" alt="Logo" className="w-12" />
          <h1 className="text-2xl font-bold">ConnectingNepali Dashboard</h1>
        </div>
        <button
          onClick={handleLogout}
          className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
        >
          Logout
        </button>
      </div>

      {/* News Ticker */}
      <div className="overflow-hidden bg-yellow-100 text-black py-2 px-4 rounded shadow mb-6">
        <div className="whitespace-nowrap animate-marquee text-sm font-medium">
          {news.map((item, i) => (
            <span key={i} className="inline-block mr-10">📰 {item}</span>
          ))}
        </div>
      </div>

      {/* Services Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {services.map((service, index) => (
          <div key={index} className="bg-white p-6 rounded shadow hover:shadow-lg transition">
            <div className="text-4xl mb-2">{service.icon}</div>
            <h2 className="text-xl font-semibold">{service.name}</h2>
            <p className="text-gray-600">{service.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
