import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function LoginPage() {
  const [form, setForm] = useState({ email: '', password: '' });
  const [registerForm, setRegisterForm] = useState({ name: '', email: '', password: '' });
  const [message, setMessage] = useState('');
  const [showLogin, setShowLogin] = useState(false);
  const [showRegister, setShowRegister] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleRegisterChange = (e) => {
    setRegisterForm({ ...registerForm, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('https://connectingnepali.onrender.com/api/auth/login', form);
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      window.location.href = '/dashboard';
    } catch (err) {
      setMessage(err.response?.data?.error || 'Login failed');
    }
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('https://connectingnepali.onrender.com/api/auth/register', registerForm);
      setMessage('Registration successful! Redirecting to login...');
      setShowRegister(false);
      setShowLogin(true);
    } catch (err) {
      setMessage(err.response?.data?.error || 'Registration failed');
    }
  };

  return (
    <div className="min-h-screen bg-white">
      {/* Top Header */}
      <div className="flex justify-between items-center p-4 shadow-md">
        <div className="text-lg font-bold">ConnectingNepali</div>
        <div className="space-x-4">
          <button onClick={() => setShowLogin(true)} className="px-4 py-2 border rounded">Login</button>
          <button onClick={() => setShowRegister(true)} className="px-4 py-2 border rounded">Register</button>
        </div>
      </div>

      {/* Home Banner Section */}
      <div className="bg-gray-100 px-6 py-8 text-center">
        <h1 className="text-4xl font-bold mb-2">Welcome to ConnectingNepali</h1>
        <p className="text-lg mb-6">Your one-stop platform for Nepali immigrants in Canada.</p>
        <button onClick={() => setShowRegister(true)} className="bg-blue-600 text-white px-6 py-3 rounded hover:bg-blue-700">Get Started</button>
      </div>

      {/* Service Grid */}
      <div className="p-8">
        <h2 className="text-2xl font-semibold mb-4">Explore Services</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {[
            { title: 'New in Canada', desc: 'Arrival guide and newcomer essentials', path: '/services/new-to-canada' },
            { title: 'Airport Pickup & Settlement Help', desc: 'Smooth landing and support from day 1', path: '/services/airport-pickup-settlement-help' },
            { title: 'Housing Assistance', desc: 'Shared rooms, rental tips, and landlord advice', path: '/services/housing-assistance' },
            { title: 'SIN, Bank, ID Setup', desc: 'Documents and accounts made easy', path: '/services/sin-bank-id-setup' },
            { title: 'Student Support', desc: 'Colleges, PGWP, study visa help', path: '/services/student-support' },
            { title: 'Job Connection', desc: 'Resumes, job boards, and referrals', path: '/services/job-connection' },
            { title: 'Community Support', desc: 'Meetups, culture and connection', path: '/services/community-support' },
            { title: 'Language Support', desc: 'IELTS prep, French and translation', path: '/services/language-support' },
            { title: 'Nepali Directory', desc: 'Shops, food, services near you', path: '/services/nepali-directory' },
            { title: 'Legal/Immigration Help', desc: 'PR pathway, lawyers and resources', path: '/services/legal-immigration-help' }
          ].map((service, idx) => (
            <div key={idx} className="border p-4 rounded shadow hover:shadow-md transition">
              <h3 className="font-semibold text-lg mb-1">{service.title}</h3>
              <p className="text-sm text-gray-600 mb-2">{service.desc}</p>
              <button
                onClick={() => navigate(service.path)}
                className="text-blue-600 hover:underline text-sm"
              >
                Learn More
              </button>
            </div>
          ))}
        </div>
      </div>

      {/* Login Modal */}
      {showLogin && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow-md w-full max-w-md relative">
            <button onClick={() => setShowLogin(false)} className="absolute top-2 right-2 text-gray-500">✕</button>
            <h2 className="text-xl font-bold mb-4 text-center">Login to Your Account</h2>
            {message && <p className="mb-3 text-red-500 text-center">{message}</p>}
            <form onSubmit={handleSubmit}>
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={form.email}
                onChange={handleChange}
                className="mb-3 w-full px-3 py-2 border rounded"
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={form.password}
                onChange={handleChange}
                className="mb-3 w-full px-3 py-2 border rounded"
                required
              />
              <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700">
                Login
              </button>
            </form>
          </div>
        </div>
      )}

      {/* Register Modal */}
      {showRegister && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-6 rounded shadow-md w-full max-w-md relative">
            <button onClick={() => setShowRegister(false)} className="absolute top-2 right-2 text-gray-500">✕</button>
            <h2 className="text-xl font-bold mb-4 text-center">Create an Account</h2>
            {message && <p className="mb-3 text-red-500 text-center">{message}</p>}
            <form onSubmit={handleRegisterSubmit}>
              <input
                type="text"
                name="name"
                placeholder="Full Name"
                value={registerForm.name}
                onChange={handleRegisterChange}
                className="mb-3 w-full px-3 py-2 border rounded"
                required
              />
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={registerForm.email}
                onChange={handleRegisterChange}
                className="mb-3 w-full px-3 py-2 border rounded"
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={registerForm.password}
                onChange={handleRegisterChange}
                className="mb-3 w-full px-3 py-2 border rounded"
                required
              />
              <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700">
                Register
              </button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
