import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function AuthPage() {
  const [activeTab, setActiveTab] = useState('login');
  const [loginForm, setLoginForm] = useState({ email: '', password: '' });
  const [registerForm, setRegisterForm] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
  });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleLoginChange = (e) =>
    setLoginForm({ ...loginForm, [e.target.name]: e.target.value });

  const handleRegisterChange = (e) =>
    setRegisterForm({ ...registerForm, [e.target.name]: e.target.value });

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', loginForm);
      localStorage.setItem('token', res.data.token);
      setMessage('');
      navigate('/dashboard');
    } catch (err) {
      setMessage(err.response?.data?.error || 'Login failed');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    if (registerForm.password !== registerForm.confirmPassword) {
      setMessage('Passwords do not match');
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/auth/register', {
        name: registerForm.name,
        email: registerForm.email,
        password: registerForm.password,
      });
      setMessage('Registration successful! You can now login.');
      setActiveTab('login');
    } catch (err) {
      setMessage(err.response?.data?.error || 'Registration failed');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <div className="bg-white shadow-md p-6 w-80 rounded-md">
        {/* Tabs */}
        <div className="flex mb-4 rounded overflow-hidden">
          <button
            className={`w-1/2 py-2 ${activeTab === 'login' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-black'}`}
            onClick={() => setActiveTab('login')}
          >
            Login
          </button>
          <button
            className={`w-1/2 py-2 ${activeTab === 'register' ? 'bg-blue-600 text-white' : 'bg-gray-200 text-black'}`}
            onClick={() => setActiveTab('register')}
          >
            Register
          </button>
        </div>

        {message && <p className="text-sm text-red-500 mb-3">{message}</p>}

        {/* Form */}
        <form onSubmit={activeTab === 'login' ? handleLogin : handleRegister} className="space-y-3">
          {activeTab === 'register' && (
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={registerForm.name}
              onChange={handleRegisterChange}
              className="w-full px-3 py-2 border rounded"
              required
            />
          )}
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={activeTab === 'login' ? loginForm.email : registerForm.email}
            onChange={activeTab === 'login' ? handleLoginChange : handleRegisterChange}
            className="w-full px-3 py-2 border rounded"
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={activeTab === 'login' ? loginForm.password : registerForm.password}
            onChange={activeTab === 'login' ? handleLoginChange : handleRegisterChange}
            className="w-full px-3 py-2 border rounded"
            required
          />
          {activeTab === 'register' && (
            <input
              type="password"
              name="confirmPassword"
              placeholder="Confirm Password"
              value={registerForm.confirmPassword}
              onChange={handleRegisterChange}
              className="w-full px-3 py-2 border rounded"
              required
            />
          )}
          <button
            type="submit"
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
          >
            {activeTab === 'login' ? 'Login' : 'Register'}
          </button>
        </form>
      </div>
    </div>
  );
}
