import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { GoogleLogin } from '@react-oauth/google';
//import jwtDecode from 'jwt-decode';

export default function LoginUI() {
  const [form, setForm] = useState({ email: '', password: '' });
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('https://connectingnepali.onrender.com/api/auth/login', form);
      localStorage.setItem('token', res.data.token);
      setMessage('Login successful!');
      navigate('/dashboard');
    } catch (err) {
      setMessage(err.response?.data?.error || 'Login failed');
    }
  };

  const handleGoogleLogin = async (credentialResponse) => {
    try {
      const decoded = jwtDecode(credentialResponse.credential);
      const res = await axios.post('https://connectingnepali.onrender.com/api/auth/google', {
        token: credentialResponse.credential,
      });

      localStorage.setItem('token', res.data.token);
      navigate('/dashboard');
    } catch (err) {
      setMessage('Google login failed');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 relative">
      {/* Top-right nav buttons */}
      <div className="absolute top-4 right-4 flex gap-4">
        <button
          onClick={() => navigate('/login-ui')}
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Login
        </button>
        <button
          onClick={() => navigate('/register')}
          className="bg-gray-600 text-white px-4 py-2 rounded hover:bg-gray-700"
        >
          Register
        </button>
      </div>

      {/* Centered login card */}
      <div className="flex items-center justify-center h-screen">
        <form onSubmit={handleSubmit} className="bg-white p-8 rounded shadow-md w-96 space-y-4">
          <h2 className="text-2xl font-bold text-center">Login</h2>
          {message && <p className="text-red-500 text-center">{message}</p>}

          <input
            type="email"
            name="email"
            placeholder="Email"
            value={form.email}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded"
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={form.password}
            onChange={handleChange}
            className="w-full px-3 py-2 border rounded"
            required
          />
          <button type="submit" className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700">
            Login
          </button>

          {/* Google login (non-destructive) */}
          <div className="text-center text-sm text-gray-500">or</div>
          <div className="flex justify-center">
            <GoogleLogin
              onSuccess={handleGoogleLogin}
              onError={() => setMessage('Google Sign In failed')}
            />
          </div>
        </form>
      </div>
    </div>
  );
}
