import React, { useEffect, useState } from 'react';

export default function Dashboard() {
  const [user, setUser] = useState({});
  const [isNewToCanada, setIsNewToCanada] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    const canadaStatus = localStorage.getItem('isNewToCanada');

    if (storedUser && storedUser !== 'undefined') {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        if (!canadaStatus) {
          setShowModal(true); // only show modal if not answered yet
        } else {
          setIsNewToCanada(canadaStatus);
        }
      } catch (err) {
        console.error('Failed to parse user:', err);
      }
    }
  }, []);

  const handleResponse = async (response) => {
    try {
      // Save to MongoDB backend
      const res = await fetch('http://localhost:5000/api/user/canada-status', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
        body: JSON.stringify({
          userId: user._id,
          isNewToCanada: response,
        }),
      });

      if (res.ok) {
        localStorage.setItem('isNewToCanada', response);
        setIsNewToCanada(response);
        setShowModal(false);
      } else {
        alert('Error saving your answer. Please try again.');
      }
    } catch (err) {
      console.error('Request failed:', err);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    window.location.href = '/';
  };

  return (
    <div className="p-10">
      <h1 className="text-2xl font-bold mb-4">Welcome, {user.name || 'Guest'}!</h1>

      {isNewToCanada && (
        <p className="text-lg mb-4">
          You answered: <strong>{isNewToCanada === 'yes' ? 'Yes' : 'No'}</strong>. 
          {isNewToCanada === 'yes'
            ? ' Welcome to your new life in Canada!'
            : ' Great to see you settling in!'}
        </p>
      )}

      {showModal && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg max-w-sm w-full">
            <h2 className="text-xl font-semibold mb-4 text-center">
              Are you new to Canada?
            </h2>
            <div className="flex justify-around">
              <button
                onClick={() => handleResponse('yes')}
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
              >
                Yes
              </button>
              <button
                onClick={() => handleResponse('no')}
                className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
              >
                No
              </button>
            </div>
          </div>
        </div>
      )}

      <button
        onClick={handleLogout}
        className="mt-6 block text-red-600 underline"
      >
        Logout
      </button>
    </div>
  );
}
