import React, { useEffect, useState } from 'react';

export default function Dashboard() {
  const [user, setUser] = useState({});

  useEffect(() => {
    const storedUser = localStorage.getItem('user');

    if (storedUser && storedUser !== "undefined") {
      try {
        setUser(JSON.parse(storedUser));
      } catch (error) {
        console.error("Failed to parse user from localStorage:", error);
        setUser({});
      }
    }
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    window.location.href = '/';
  };

  return (
    <div className="p-10">
      <h1 className="text-2xl font-bold mb-4">Welcome, {user.name || 'Guest'}!</h1>
      <p className="text-lg mb-4">Are you new to Canada?</p>
      <div className="space-x-4">
        <button onClick={() => alert('Great! Welcome!')} className="bg-blue-500 text-white px-4 py-2 rounded">Yes</button>
        <button onClick={() => alert('Glad to have you here!')} className="bg-green-500 text-white px-4 py-2 rounded">No</button>
      </div>
      <button onClick={handleLogout} className="mt-6 block text-red-500">Logout</button>
    </div>
  );
}
