import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function Dashboard() {
  const navigate = useNavigate();

  // ✅ Safely get full name from localStorage
  const user = (() => {
    try {
      const stored = localStorage.getItem('user');
      return stored ? JSON.parse(stored) : { name: 'Guest' };
    } catch {
      return { name: 'Guest' };
    }
  })();

  // ✅ Clock state
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    const interval = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(interval);
  }, []);

  const formattedTime = currentTime.toLocaleTimeString();

  // ✅ Logout function
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/');
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-6">
      <div className="bg-white shadow-md rounded p-6 w-full max-w-xl text-center">
        <h1 className="text-2xl font-bold mb-2">Hello, {user.name}</h1>
        <p className="text-gray-600 text-lg mb-6">
          Current Time: <span className="font-semibold">{formattedTime}</span>
        </p>

        <button
          onClick={handleLogout}
          className="bg-red-600 hover:bg-red-700 text-white px-6 py-2 rounded"
        >
          Logout
        </button>
      </div>
    </div>
  );
}
