// src/pages/LoginUI.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import { GoogleLogin } from '@react-oauth/google';
import jwtDecode from 'jwt-decode';

export default function LoginUI() {
  const navigate = useNavigate();

  const handleGoogleLoginSuccess = (credentialResponse) => {
    const user = jwtDecode(credentialResponse.credential);
    localStorage.setItem('user', JSON.stringify(user));
    localStorage.setItem('token', credentialResponse.credential); // optional
    navigate('/dashboard');
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center px-4">
      <div className="bg-white border border-gray-200 rounded-xl shadow-md p-10 max-w-md w-full text-center">
        <h1 className="text-3xl font-bold text-blue-700 mb-6">Welcome to ConnectingNepali</h1>

        <div className="flex justify-center gap-4 mb-6">
          <button
            onClick={() => navigate('/register')}
            className="px-6 py-2 bg-green-600 text-white rounded-lg shadow hover:bg-green-700 transition"
          >
            Register
          </button>
          <button
            onClick={() => navigate('/')}
            className="px-6 py-2 bg-blue-600 text-white rounded-lg shadow hover:bg-blue-700 transition"
          >
            Login
          </button>
        </div>

        <GoogleLogin
          onSuccess={handleGoogleLoginSuccess}
          onError={() => console.log('Google Login Failed')}
        />
      </div>
    </div>
  );
}
