import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function Dashboard() {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user')) || { name: 'User' };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  return (
    <div className="flex min-h-screen">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-800 text-white p-6 space-y-4 flex flex-col justify-between">
        <div>
          <h2 className="text-2xl font-bold mb-6">ConnectingNepali</h2>
          <nav className="space-y-2">
            <a href="#" className="block hover:bg-gray-700 p-2 rounded">Dashboard</a>
            <a href="#" className="block hover:bg-gray-700 p-2 rounded">Users</a>
            <a href="#" className="block hover:bg-gray-700 p-2 rounded">Settings</a>
          </nav>
        </div>

        {/* ✅ Logout Button */}
        <button
          onClick={handleLogout}
          className="w-full bg-red-600 hover:bg-red-700 text-white py-2 mt-8 rounded"
        >
          Logout
        </button>
      </aside>

      {/* Main Content */}
      <main className="flex-1 bg-gray-100 p-8">
        <header className="mb-6">
          <h1 className="text-3xl font-bold">Welcome, {user.name}</h1>
          <p className="text-gray-600">Here’s what’s happening today</p>
        </header>

        {/* Widgets */}
        <section className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          <div className="bg-white p-6 rounded shadow">
            <h2 className="text-lg font-semibold">Visitors</h2>
            <p className="text-2xl font-bold">1,245</p>
          </div>
          <div className="bg-white p-6 rounded shadow">
            <h2 className="text-lg font-semibold">Posts</h2>
            <p className="text-2xl font-bold">320</p>
          </div>
          <div className="bg-white p-6 rounded shadow">
            <h2 className="text-lg font-semibold">New Signups</h2>
            <p className="text-2xl font-bold">82</p>
          </div>
        </section>
      </main>
    </div>
  );
}
