import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function LoginUI() {
  const [form, setForm] = useState({ email: '', password: '' });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleLogin = async (e) => {
    e.preventDefault();

    // ✅ MOCK: Replace this with your real API call if needed
    if (form.email === 'test@example.com' && form.password === '123456') {
      const user = {
        _id: '6647c3813c61a80014b7c155',
        name: 'Ashima',
        email: form.email
      };

      const token = 'mock-jwt-token-123';

      // ✅ Save to localStorage (required by Dashboard.js)
      localStorage.setItem('user', JSON.stringify(user));
      localStorage.setItem('token', token);
      localStorage.removeItem('isNewToCanada'); // reset to show modal again

      navigate('/dashboard');
    } else {
      setError('❌ Invalid credentials');
    }
  };

  return (
    <div className="p-10 max-w-md mx-auto">
      <h2 className="text-2xl font-bold mb-4">Login</h2>
      <form onSubmit={handleLogin} className="space-y-4">
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          className="border px-3 py-2 w-full rounded"
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={form.password}
          onChange={handleChange}
          className="border px-3 py-2 w-full rounded"
          required
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded w-full"
        >
          Login
        </button>
        {error && <p className="text-red-600 text-sm">{error}</p>}
      </form>
    </div>
  );
}
